// Prepare output payload
let servers_info = [];

// Check if payload is actually a string, and if it is the expected output
if (typeof msg.payload === "string" || msg.payload instanceof String) {
    if (msg.payload.indexOf("Currently running servers:") >= 0) {
        // Create array of servers 
        let server_list = msg.payload.split("\r\n").slice(1, -1);

        // Parse each server string to get server info
        server_list.forEach(function(item, index){
            let url_stub = item.split(":")[1];
            let server_info = {
                "baseUrl": "http:" + url_stub,
                "wsUrl": "ws:" + url_stub,
                "port": item.split(":")[2].split("/")[0],
                "token": item.split("?token=")[1].split(" :: ")[0],
                "dir": item.split(" :: ")[1],
            }
            servers_info.push(server_info);
        });
    }
}

// Set servers info as message payload
msg.payload = servers_info;

return msg;

// Check if message payload is actually an array
if(Array.isArray(msg.payload)) {
    let servers = RED.util.cloneMessage(msg).payload;

    // Stop session
    if (global.get("jupyter_session") != undefined) {
        global.get("jupyter_session").shutdown();
    }

    // Stop all found servers
    servers.forEach(function(item) {
        if (typeof item.port === 'string' || item.port instanceof String) {
            msg.payload = item.port;
            node.send(msg);
        }
    });

    // Clear globals
    global.set("jupyter_port", undefined);
    global.set("jupyter_token", undefined);
    global.set("jupyter_wsUrl", undefined);
    global.set("jupyter_baseUrl", undefined);
    global.set("jupyter_session", undefined);
}

// Check if message payload is actually an array
if(Array.isArray(msg.payload)) {
    let servers = RED.util.cloneMessage(msg).payload;

    // Check if any of the running servers was invoked from here
    servers.forEach(function(item) {
        if (typeof item.port === 'string' || item.port instanceof String) {
            if (item.port == global.get("jupyter_port")) {
                // If it was, stop session, the server, and clear globals
                if (global.get("jupyter_session") != undefined) {
                    global.get("jupyter_session").shutdown();
                }
                global.set("jupyter_port", undefined);
                global.set("jupyter_token", undefined);
                global.set("jupyter_wsUrl", undefined);
                global.set("jupyter_baseUrl", undefined);
                global.set("jupyter_session", undefined);

                msg.payload = item.port;
                node.send(msg);
            }
        }
    });
}

let is_running = false;

// Check if there is information of a running server
if (global.get("jupyter_port") != undefined) {
    // If so, check if it is actually running

    // Check if message payload is actually an array
    if(Array.isArray(msg.payload)) {
        // Check if global port corresponds to a running server
        msg.payload.forEach(function(item) {
            if (typeof item.port === 'string' || item.port instanceof String) {
                if (item.port == global.get("jupyter_port")) {
                    // If so, re-set globals and check session status
                    is_running = true;
                    global.set("jupyter_port", item.port);
                    global.set("jupyter_token", item.token);
                    global.set("jupyter_wsUrl", item.wsUrl);
                    global.set("jupyter_baseUrl", item.baseUrl);

                    if (global.get("jupyter_session") == undefined) {
                        msg.payload = "start session";
                    } else {
                        if (global.get("jupyter_session").serverSettings.token != item.token) {
                            global.get("jupyter_session").shutdown();
                            global.set("jupyter_session", undefined);
                            msg.payload = "start session";
                        } else {
                            msg.payload = "";
                        }
                    }
                }
            }
        });
    }
}

// If not, just go on starting a new one
if (!is_running) {
    // Clear all globals and session info
    if (global.get("jupyter_session") != undefined) {
        global.get("jupyter_session").shutdown();
    }
    global.set("jupyter_port", undefined);
    global.set("jupyter_token", undefined);
    global.set("jupyter_wsUrl", undefined);
    global.set("jupyter_baseUrl", undefined);
    global.set("jupyter_session", undefined);

    msg.payload = "start server";
}

return msg;

let main_identifier = "copy and paste one of these URLs:\r\n"
if (typeof msg.payload === 'string' || msg.payload instanceof String) {
    if (msg.payload.indexOf(main_identifier) >= 0) {
        let full_url = msg.payload.split(main_identifier)[1].split("\r\n")[0];
        let url_stub = full_url.split(":")[1];
        global.set("jupyter_port", full_url.split(":")[2].split("/")[0]);
        global.set("jupyter_token", full_url.split("?token=")[1].split(" :: ")[0]);
        global.set("jupyter_wsUrl", "ws:" + url_stub);
        global.set("jupyter_baseUrl", "http:" + url_stub);

        msg.payload = "start session";
        return msg;
    }
}

// Import Jupyter Services
// const services = require('@jupyterlab/services');
const services = global.get('jp_services');

if (msg.payload == "start session") {
    // Check if session exists, and end it
    let resetting = false;
    if (global.get("jupyter_session") != undefined) {
        resetting = true;
        console.log('Ending session…');
        global.get("jupyter_session").shutdown().then(function () {
            global.set("jupyter_session", undefined);
            new_session();
        });
    }

    // Start a new session.
    function new_session() {
        const options = {
            path: 'foo.ipynb',
            type: 'notebook',
            name: 'foo.ipynb',
            kernel: {
                name: 'python'
            }
        };

        let port = global.get("jupyter_port");
        let token = global.get("jupyter_token");
        let baseUrl = global.get("jupyter_baseUrl") + ":" + port + "/";
        let wsUrl = global.get("jupyter_wsUrl") + ":" + port + "/";
        console.log('Starting session…');

        const kernelManager = new services.KernelManager();
        kernelManager.serverSettings.baseUrl = baseUrl;
        kernelManager.serverSettings.wsUrl = wsUrl;
        kernelManager.serverSettings.token = token;

        const sessionManager = new services.SessionManager({ kernelManager });
        sessionManager.serverSettings.baseUrl = baseUrl;
        sessionManager.serverSettings.wsUrl = wsUrl;
        sessionManager.serverSettings.token = token;

        sessionManager.startNew(options).then(function (s) {
            global.set("jupyter_session", s);
            node.send(msg);
        });
    }

    if (!resetting) {
        new_session();
    }
}

let is_running = false;

// Check if there is information of a running server
if (global.get("jupyter_port") != undefined) {
    // If so, check if it is actually running
    // Check if message payload is actually an array
    if(Array.isArray(msg.payload)) {
        // Check if global port corresponds to a running server
        msg.payload.forEach(function(item) {
            if (typeof item.port === 'string' || item.port instanceof String) {
                if (item.port == global.get("jupyter_port")) {
                    is_running = true;
                }
            }
        });
    }
}

// Set status
if (is_running) {
    msg.payload = "Active on port " + global.get("jupyter_port");
} else {
    msg.payload = "Stopped";
}
return msg;

// Check if there is information of a running server
if (global.get("jupyter_port") != undefined) {
    // If so, check if it is actually running
    // Check if message payload is actually an array
    if(Array.isArray(msg.payload)) {
        // Check if global port corresponds to a running server
        msg.payload.forEach(function(item) {
            if (typeof item.port === 'string' || item.port instanceof String) {
                if (item.port == global.get("jupyter_port")) {
                    // If so, re-set globals and check session status
                    global.set("jupyter_port", item.port);
                    global.set("jupyter_token", item.token);
                    global.set("jupyter_wsUrl", item.wsUrl);
                    global.set("jupyter_baseUrl", item.baseUrl);

                    msg.payload = "start session";
                }
            }
        });
    }
} else {
    msg.payload = ""
}

return msg;

let session = global.get("jupyter_session");

if (session != undefined) {
    const future = session.kernel.requestExecute({ code: msg.payload, silent: false });
    future.onIOPub = function(reply) {
        if (reply.header.msg_type == 'stream') {
            msg.payload = reply.content.text;
            while (msg.payload.slice(-1) == "\n" || msg.payload.slice(-1) == "\r") {
                msg.payload = msg.payload.slice(0, -1);
            }

            if (reply.content.text.length > 1000) {
                console.log(
                    reply.content.text.substring(0, 499) +
                    "\n...\n" +
                    reply.content.text.substring(reply.content.text.length - 500)
                );
            } else {
                console.log(reply.content.text);
            }

        }
    };
    future.onReply = function(reply) {
        if (reply.content.status == 'ok') {
            msg.code = 0;
            // console.log('Python execution OK!');
        }
        else {
            console.log("Something went wrong with the python execution:");
            console.log(reply.content.evalue);
            msg.code = 1;
        }
    };
    future.done.then(function(){
        node.send(msg);
    }).catch(function (err) {
        console.error(err);
    });

    msg.payload = "";
}

