def convert_np(var):
    # If numpy is not installed, it does nothing
    changed = False
    if npok:
        if isinstance(var, np.ndarray):
            var = var.tolist()
            changed = True
    return var, changed

# If it is a pandas dataframe, convert it to a json string
def convert_df(var):
    # If pandas is not installed, it does nothing
    changed = False
    if pdok:
        if isinstance(var, pd.DataFrame):
            var = var.to_json()
            changed = True
    return var, changed

# If it is a dict, list or tuple, recursively try to find numpy arrays and pandas dataframes to convert
def walk_list_convert(var):
    changed = False
    if isinstance(var, list) or isinstance(var, tuple):
        for i in range(len(var)):
            it = var[i]
            newit, it_changed = check_convert_all(it)
            if it_changed:
                var[i] = newit
                changed = True
    return var, changed

def walk_dict_convert(var):
    changed = False
    if isinstance(var, dict):
        for key in var:
            it = var[key]
            newit, it_changed = check_convert_all(it)
            if it_changed:
                var[key] = newit
                changed = True
    return var, changed

# Function to check all conversion possibilities
def check_convert_all(var):
    changed = False 
    var, changed = convert_np(var)
    if not changed:
        var, changed = convert_df(var)
    if not changed:
        var, changed = walk_list_convert(var)
    if not changed:
        var, changed = walk_dict_convert(var)

    return var, changed


