# We begin with a safe and efficient VISA initialization routine
visa_import_ok = False
visa_rm_ok = False
try:
    visa
    visa_import_ok = True
except:
    try:
        import pyvisa as visa
        visa_import_ok = True

        visa_open_devs = []

        # Function to aid in managing open instruments
        def FindOpenResource(res_name:str) -> visa.Resource:
            visa_rm_list = visa_rm.list_opened_resources()

            # Clear closed devices
            for res in visa_open_devs.copy():
                if res not in visa_rm_list:
                    visa_open_devs.remove(res)

            # Find desired device
            for res in visa_rm_list:
                if res.resource_info.resource_name == res_name:
                    return res
            raise Exception(f"Error: No resource matching the name {res_name} found!")

    except Exception as e:
        # print(e)
        print("Error importing pyvisa! Is it installed?")

if visa_import_ok:
    try:
        visa_rm
        visa_rm_ok = True
    except:
        try:
            visa_rm = visa.ResourceManager()
            visa_rm_ok = True
        except Exception as e:
            # print(e)
            print("Error initializing VISA Resource Manager. Is a VISA backend available? (E.g.: NI, Keysight)")

if visa_rm_ok:
    if visa_rm.list_resources:
        print("VISA initialization OK!")

# The code as shown here uses the message payload received by the node, via Mustache Template
