try:
    if visa_rm_ok:       
        try:
            # Check if open
            FindOpenResource(inst_addr)
            print(f"Resource at {inst_addr} was already open!")
        except:
            try:
                # Try opening device
                visa_open_devs.append(visa_rm.open_resource(inst_addr))

                # If communicating via socket, set termination to line end
                if "::SOCKET" in inst_addr:
                    visa_open_devs[-1].read_termination = "\n"

                print(f"Successfully opened resource at {inst_addr}.")

            except Exception as e:
                print(f"Error opening device:\n{e}")
    else:
        print("VISA initialization not complete, check previous output!")
